const currentTitle = urlParams.get('title');

function getAdjacentHymn(direction) {
    const titles = Object.keys(hymnsData);
    const currentIndex = titles.indexOf(currentTitle);

    if (direction === 'next' && currentIndex < titles.length - 1) {
        return titles[currentIndex + 1];
    } else if (direction === 'previous' && currentIndex > 0) {
        return titles[currentIndex - 1];
    }
    return null;
}

function goToNextSong() {
    const nextHymn = getAdjacentHymn('next');
    if (nextHymn) {
        window.location.replace(`LAEp.html?title=${encodeURIComponent(nextHymn)}&from=button`);
    }
}

function goToPreviousSong() {
    const previousHymn = getAdjacentHymn('previous');
    if (previousHymn) {
        window.location.replace(`LAEp.html?title=${encodeURIComponent(previousHymn)}&from=button`);
    }
}


let touchStartX = 0;
let touchEndX = 0;

document.addEventListener('touchstart', (e) => {
    touchStartX = e.touches[0].clientX;
});

document.addEventListener('touchend', (e) => {
    touchEndX = e.changedTouches[0].clientX;
    handleSwipe();
});

function handleSwipe() {
    const swipeDistance = touchStartX - touchEndX;
    if (swipeDistance > 100) {
        const nextHymn = getAdjacentHymn('next');
        if (nextHymn) {
            window.location.replace(`LAEp.html?title=${encodeURIComponent(nextHymn)}&from=swipe`);
        }
    } else if (swipeDistance < -100) {
        const previousHymn = getAdjacentHymn('previous');
        if (previousHymn) {
            window.location.replace(`LAEp.html?title=${encodeURIComponent(previousHymn)}&from=swipe`);
        }
    }
}



   function displayLyrics(title) {
    const lyricsDiv = document.getElementById('hlyrics');
    const hymnTitleElement = document.getElementById('hymn-title');
    hymnTitleElement.innerText = title;
    lyricsDiv.innerText = hymnsData[title] || "Paroles non trouvées.";
}


if (hymnTitle && hymnsData[hymnTitle]) {
    displayLyrics(hymnTitle);
} else {
    document.getElementById('hlyrics').innerText = "Cantique non trouvé.";
}

